plates = [
    "NeoExp2Plate1",
    "NeoExp2Plate2",
    "NeoExp3Plate1",
    "NeoExp3Plate2",
    "NeoExp4Plate1",
    "NeoExp4Plate2",
]


def well_to_index(plate, xy=None, well=None):
    if xy is None and well is None:
        raise ValueError("Either xy or well must be passed")
    elif well is not None:
        xy = keyence.well_to_xy(well)

    # use xy and plate to produce index
    return plates.index(plate) * 60 + xy
